mapper.insert:

  SELECT LAST_INSERT_ID()

insert into article_part (context, sort, article_id
  )
values (#{context,jdbcType=VARCHAR}, #{sort,jdbcType=INTEGER}, #{articleId,jdbcType=INTEGER}
  )

mapper.update:
update article_part
set context = #{context,jdbcType=VARCHAR},
  sort = #{sort,jdbcType=INTEGER},
  article_id = #{articleId,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, context, sort, article_id
from article_part
where id = #{id,jdbcType=INTEGER}
