mapper.insert:

  SELECT LAST_INSERT_ID()

insert into image (sort, url, article_id
  )
values (#{sort,jdbcType=INTEGER}, #{url,jdbcType=VARCHAR}, #{articleId,jdbcType=INTEGER}
  )

mapper.update:
update image
set sort = #{sort,jdbcType=INTEGER},
  url = #{url,jdbcType=VARCHAR},
  article_id = #{articleId,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, sort, url, article_id
from image
where id = #{id,jdbcType=INTEGER}
